import React, { useState, useEffect } from 'react';

function Login() {
    const [formData, setFormData] = useState({
        username: '',
        password: ''
    });
    const [rememberUsername, setRememberUsername] = useState(false);

    useEffect(() => {
        const savedUsername = localStorage.getItem('username');
        if (savedUsername) {
            setFormData((prev) => ({ ...prev, username: savedUsername }));
            setRememberUsername(true);
        }
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleCheckboxChange = () => {
        setRememberUsername(!rememberUsername);
        if (rememberUsername) {
            localStorage.removeItem('username');
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (rememberUsername) {
            localStorage.setItem('username', formData.username);
        }
        // 로그인 처리 로직 추가
        console.log('Form submitted:', formData);
    };

    return (
        <div className="container mt-5 d-flex justify-content-center">
            <div className="card shadow-sm" style={{ width: '30rem' }}>
                <div className="card-body">
                    <h4 className="card-title text-center mb-4">로그인</h4>
                    <form onSubmit={handleSubmit}>
                        <div className="mb-3">
                            <label htmlFor="username" className="form-label"><strong>아이디:</strong></label>
                            <input
                                type="text"
                                id="username"
                                className="form-control"
                                name="username"
                                value={formData.username}
                                onChange={handleChange}
                                required
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="password" className="form-label"><strong>비밀번호:</strong></label>
                            <input
                                type="password"
                                id="password"
                                className="form-control"
                                name="password"
                                value={formData.password}
                                onChange={handleChange}
                                required
                            />
                        </div>

                        <div className="mb-3">
                            <input
                                type="checkbox"
                                id="rememberUsername"
                                className="form-check-input"
                                checked={rememberUsername}
                                onChange={handleCheckboxChange}
                            />
                            <label htmlFor="rememberUsername" className="form-check-label">아이디 저장하기</label>
                        </div>

                        <hr />
                        <div className="d-flex justify-content-between">
                            <button type="submit" className="btn btn-success">로그인</button>
                            <a href="/products/all" className="btn btn-secondary">뒤로가기</a>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Login;
