import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Join() {
    const [formData, setFormData] = useState({
        userId: '',
        password: '',
    });

    const [error, setErrors] = useState({});
    const [valid, setValid] = useState(true);
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        let isValid = true;
        let validationErrors = {};

        // 유효성 검사
        if (formData.userId === "" || formData.userId === null) {
            isValid = false;
            validationErrors.userId = "아이디를 입력하세요.";
        }
        if (formData.password === "" || formData.password === null) {
            isValid = false;
            validationErrors.password = "비밀번호를 입력하세요.";
        }
        setErrors(validationErrors);
        setValid(isValid);

        if (isValid) {
            try {
                // 서버에서 모든 사용자 데이터 가져오기
                const result = await axios.get('http://localhost:8000/users');
                const users = result.data;

                // 최대 ID 계산
                const maxId = users.length > 0
                    ? Math.max(...users.map(user => parseInt(user.id, 10)))
                    : 0;

                // 중복 아이디 확인
                const userExists = users.some(user => user.userId === formData.userId);

                if (userExists) {
                    setErrors({ userId: "이미 존재하는 아이디입니다." });
                    setValid(false);
                } else {
                    // 새 사용자 추가
                    const newUser = {
                        id: (maxId + 1).toString(), // 새로운 ID
                        ...formData
                    };

                    await axios.post('http://localhost:8000/users', newUser);
                    alert("회원가입이 완료되었습니다.");
                    navigate('/login');
                }
            } catch (err) {
                console.error(err);
                alert("서버 요청 중 오류가 발생했습니다.");
            }
        }
    };

    return (
        <div style={{ marginTop: '5rem', display: 'flex', justifyContent: 'center' }}>
            <div style={{ width: '30rem', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', borderRadius: '8px', overflow: 'hidden', backgroundColor: '#fff' }}>
                <div style={{ padding: '2rem' }}>
                    <h4 style={{ textAlign: 'center', marginBottom: '1.5rem', fontSize: '1.5rem', color: '#333' }}>회원 가입</h4>
                    <form onSubmit={handleSubmit}>
                        <div>
                            <label htmlFor="userId" style={{ fontWeight: 'bold', marginBottom: '0.5rem', display: 'block', color: '#555' }}>아이디:</label>
                            <input
                                type="text"
                                id="userId"
                                name="userId"
                                value={formData.userId}
                                onChange={handleChange}
                                required
                                style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem', border: '1px solid #ccc', borderRadius: '4px', boxSizing: 'border-box' }}
                            />
                            {error.userId && <span style={{ color: 'red' }}>{error.userId}</span>}
                        </div>

                        <div>
                            <label htmlFor="password" style={{ fontWeight: 'bold', marginBottom: '0.5rem', display: 'block', color: '#555' }}>비밀번호:</label>
                            <input
                                type="password"
                                id="password"
                                name="password"
                                value={formData.password}
                                onChange={handleChange}
                                required
                                style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem', border: '1px solid #ccc', borderRadius: '4px', boxSizing: 'border-box' }}
                            />
                            {error.password && <span style={{ color: 'red' }}>{error.password}</span>}
                        </div>
                        <hr />
                        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                            <button
                                type="submit"
                                style={{ backgroundColor: '#28a745', color: '#fff', border: 'none', padding: '0.5rem 1rem', borderRadius: '4px', cursor: 'pointer', fontSize: '1rem' }}
                                onMouseOver={(e) => e.target.style.backgroundColor = '#218838'}
                                onMouseOut={(e) => e.target.style.backgroundColor = '#28a745'}
                            >
                                가입하기
                            </button>
                            <button
                                type="button"
                                style={{ backgroundColor: '#6c757d', color: '#fff', padding: '0.5rem 1rem', borderRadius: '4px', cursor: 'pointer', fontSize: '1rem' }}
                                onClick={() => navigate('/')}
                                onMouseOver={(e) => e.target.style.backgroundColor = '#5a6268'}
                                onMouseOut={(e) => e.target.style.backgroundColor = '#6c757d'}
                            >
                                뒤로가기
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Join;
