import React, { useState } from 'react';

function Join() {
    const [formData, setFormData] = useState({
        username: '',
        password: '',
        email: '',
        phonenumber: '',
        addr: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // 폼 데이터 처리 로직 추가
        console.log('Form submitted:', formData);
    };

    return (
        <div className="container mt-5 d-flex justify-content-center">
            <div className="card shadow-sm" style={{ width: '30rem' }}>
                <div className="card-body">
                    <h4 className="card-title text-center mb-4">회원 가입</h4>
                    <form onSubmit={handleSubmit}>
                        <div className="mb-3">
                            <label htmlFor="username" className="form-label"><strong>아이디:</strong></label>
                            <input
                                type="text"
                                id="username"
                                className="form-control"
                                name="username"
                                value={formData.username}
                                onChange={handleChange}
                                required
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="password" className="form-label"><strong>비밀번호:</strong></label>
                            <input
                                type="password"
                                id="password"
                                className="form-control"
                                name="password"
                                value={formData.password}
                                onChange={handleChange}
                                required
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="email" className="form-label"><strong>이메일:</strong></label>
                            <input
                                type="email"
                                id="email"
                                className="form-control"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                                required
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="phonenumber" className="form-label"><strong>전화번호:</strong></label>
                            <input
                                type="tel"
                                id="phonenumber"
                                className="form-control"
                                name="phonenumber"
                                value={formData.phonenumber}
                                onChange={handleChange}
                                pattern="^\d{3}-\d{3,4}-\d{4}$"
                                placeholder="010-1234-5678"
                                required
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="addr" className="form-label"><strong>주소:</strong></label>
                            <textarea
                                id="addr"
                                className="form-control"
                                name="addr"
                                rows="3"
                                value={formData.addr}
                                onChange={handleChange}
                                required
                            ></textarea>
                        </div>

                        <hr />
                        <div className="d-flex justify-content-between">
                            <button type="submit" className="btn btn-success">가입하기</button>
                            <a href="/products/all" className="btn btn-secondary">뒤로가기</a>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Join;
