import React, { useState } from "react";
import { chat } from './openai';
import { useNavigate } from 'react-router-dom';

function ToDoList() {
    const navigate = useNavigate();

    const [folders, setFolders] = useState([
        { id: 1, name: "개인" },
        { id: 2, name: "업무" },
        { id: 3, name: "휴지통" },
    ]);
    const [todos, setTodos] = useState({
        1: [
            { id: 1, title: "운동하기", content: "매일 아침 30분 운동", date: "2024-11-09" },
            { id: 2, title: "책 읽기", content: "소설 한 권 읽기", date: "2024-11-30" },
        ],
        2: [
            { id: 1, title: "프로젝트 보고서 작성", content: "최종 보고서 작성 및 검토", date: "2024-12-01" },
            { id: 2, title: "팀 회의 준비", content: "발표 자료 준비 및 회의록 작성", date: "2024-12-02" },
        ],
        3: []
    });
    const [favorites, setFavorites] = useState([]);
    const [selectedFolder, setSelectedFolder] = useState(1);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingTodo, setEditingTodo] = useState(null);
    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [newFolderName, setNewFolderName] = useState("");
    const [query, setQuery] = useState("");
    const [summary, setSummary] = useState("");
    const [sortOrder, setSortOrder] = useState("latest");
    const [filterDate, setFilterDate] = useState("");

    const getCurrentDate = () => {
        const now = new Date();
        const year = now.getFullYear();
        const month = String(now.getMonth() + 1).padStart(2, "0");
        const date = String(now.getDate()).padStart(2, "0");
        const hours = String(now.getHours()).padStart(2, "0");
        const minutes = String(now.getMinutes()).padStart(2, "0");
        const seconds = String(now.getSeconds()).padStart(2, "0");

        return `${year}-${month}-${date} ${hours}:${minutes}:${seconds}`;
    };

    const onAddOrUpdate = () => {
        if (title.trim() === "" || content.trim() === "") {
            alert("제목과 내용을 입력하세요!");
            return;
        }
        const folderTodos = todos[selectedFolder] || [];
        if (editingTodo) {
            const updatedTodos = folderTodos.map((todo) =>
                todo.id === editingTodo.id ? { ...todo, title, content, filterDate } : todo
            );
            setTodos({ ...todos, [selectedFolder]: updatedTodos });
        } else {
            const id = folderTodos.length > 0 ? folderTodos[folderTodos.length - 1].id + 1 : 1;
            const updatedTodos = [...folderTodos, { id, title, content, date: getCurrentDate() }];
            setTodos({ ...todos, [selectedFolder]: updatedTodos });
        }
        closeModal();
    };

    const onDelete = (id) => {
        const folderTodos = todos[selectedFolder];
        const updatedTodos = folderTodos.filter((todo) => todo.id !== id);
        const todoToDelete = folderTodos.find((todo) => todo.id === id);
        setTodos({ ...todos, [selectedFolder]: updatedTodos });
        if (todoToDelete) {
            setTodos(prevState => ({
                ...prevState,
                3: [...prevState[3], todoToDelete],
            }));
        }
    };

    const onRealDelete = (id) => {
        const folderTodos = todos[selectedFolder];
        const updatedTodos = folderTodos.filter((todo) => todo.id !== id);
        setTodos({ ...todos, [selectedFolder]: updatedTodos });
    };

    const TRASH_FOLDER_ID = 3;

    const restoreTodo = (id) => {
        const trashTodos = todos[TRASH_FOLDER_ID];
        const todoToRestore = trashTodos.find((todo) => todo.id === id);

        if (todoToRestore) {
            const originalFolder = todoToRestore.previousFolder || 1;
            setTodos((prevState) => {
                const updatedTrashTodos = trashTodos.filter((todo) => todo.id !== id);
                const updatedOriginalFolderTodos = [
                    ...prevState[originalFolder],
                    { ...todoToRestore, date: filterDate },
                ];
                return {
                    ...prevState,
                    [TRASH_FOLDER_ID]: updatedTrashTodos,
                    [originalFolder]: updatedOriginalFolderTodos,
                };
            });
        }
    };

    const toggleFavorite = (todoId) => {
        setFavorites((prev) =>
            prev.includes(todoId)
                ? prev.filter((id) => id !== todoId)
                : [...prev, todoId]
        );
    };

    const openModal = (todo = null) => {
        setIsModalOpen(true);
        if (todo) {
            setEditingTodo(todo);
            setTitle(todo.title);
            setContent(todo.content);
            setFilterDate(todo.date);
        } else {
            setEditingTodo(null);
            setTitle("");
            setContent("");
            setFilterDate("");
        }
    };

    const closeModal = () => {
        setIsModalOpen(false);
        setEditingTodo(null);
        setTitle("");
        setContent("");
        setSummary("");
        setFilterDate("");
    };

    const sortTodos = (todos) => {
        return todos.sort((a, b) =>
            sortOrder === "latest"
                ? new Date(b.date) - new Date(a.date)
                : new Date(a.date) - new Date(b.date)
        );
    };

    const filteredTodos = sortTodos(
        (todos[selectedFolder] || []).filter(
            (todo) =>
                (!filterDate || todo.date === filterDate) &&
                (todo.title.toLowerCase().includes(query.toLowerCase()) ||
                    todo.content.toLowerCase().includes(query.toLowerCase()))
        )
    );

    const fetchSummary = () => {
        const prompt = `다음은 사용자가 작성한 TodoList입니다. 내용을 간결하고, 가독성 있게 정리해주세요.
        ${content}`;

        chat(prompt, (response) => setSummary(response));
    };

    const updateTodoContent = () => {
        setContent(summary);
    };

    return (
        <div style={{ display: "flex", height: "100vh" }}>
            {/* 폴더 리스트 */}
            <div className="folder-list" style={{ flex: 1, padding: "20px", borderRight: "2px solid #ccc" }}>
                <h2>ToDoList</h2>
                <button onClick={() => navigate("/login")} className="btn-danger">
                    로그인
                </button>
                <button onClick={() => openModal()} className="btn-primary">
                    메모 쓰기
                </button>
                <ul>
                    {folders.map((folder) => (
                        <li
                            key={folder.id}
                            className={selectedFolder === folder.id ? "active" : ""}
                            onClick={() => setSelectedFolder(folder.id)}
                        >
                            {folder.name}
                        </li>
                    ))}
                </ul>
                <div>
                    <input
                        type="text"
                        value={newFolderName}
                        onChange={(e) => setNewFolderName(e.target.value)}
                        placeholder="새 폴더 이름"
                    />
                    <button
                        onClick={() => {
                            if (newFolderName.trim()) {
                                const id = folders.length > 0 ? folders[folders.length - 1].id + 1 : 1;
                                setFolders([...folders, { id, name: newFolderName.trim() }]);
                                setNewFolderName("");
                            }
                        }}
                        className="btn-success"
                    >
                        폴더 추가
                    </button>
                </div>
                <h3>{folders.find((folder) => folder.id === selectedFolder)?.name} 폴더 즐겨찾기</h3>
                {favorites.map((id) => {
                    const note = todos[selectedFolder]?.find((todo) => todo.id === id);
                    return note ? (
                        <div key={id} onClick={() => openModal(note)}>
                            {note.title}
                        </div>
                    ) : null;
                })}
            </div>

            {/* 메모 영역 */}
            <div className="todo-list" style={{ flex: 3, padding: "20px" }}>
                <h2>{folders.find((folder) => folder.id === selectedFolder)?.name}</h2>
                <input
                    type="text"
                    placeholder="검색 (제목/내용)"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                />
                <input
                    type="date"
                    value={filterDate}
                    onChange={(e) => setFilterDate(e.target.value)}
                />
                <button onClick={() => setSortOrder(sortOrder === "latest" ? "oldest" : "latest")}>
                    {sortOrder === "latest" ? "최신순" : "오래된 순"}
                </button>
                <div className="todo-items" style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
                    {filteredTodos.map((todo) => (
                        <div key={todo.id} className="todo-item" style={{ flex: "1 1 calc(33.333% - 20px)", padding: "10px", border: "1px solid #ccc" }}>
                            <h3 onClick={() => openModal(todo)}>{todo.title}</h3>
                            <p>{todo.content}</p>
                            <p>{todo.date}</p>

                            <button
                                onClick={() => toggleFavorite(todo.id)}
                                className={`btn-favorite ${favorites.includes(todo.id) ? "active" : ""}`}
                            >
                                {favorites.includes(todo.id) ? "★" : "☆"}
                            </button>
                            {selectedFolder !== 3 && (
                                <button onClick={() => onDelete(todo.id)} className="btn-delete">
                                    삭제
                                </button>
                            )}
                            {selectedFolder === 3 && (
                                <button onClick={() => restoreTodo(todo.id)} className="btn-restore">
                                    복원
                                </button>
                            )}
                            {selectedFolder === 3 && (
                                <button onClick={() => onRealDelete(todo.id)} className="btn-real-delete">
                                    완전 삭제
                                </button>
                            )}
                        </div>
                    ))}
                </div>
            </div>

            {isModalOpen && (
                <div className="modal-overlay">
                    <div className="modal">
                        <button onClick={closeModal} className="btn-close">✕</button>
                        <div>
                            <input
                                type="text"
                                value={title}
                                onChange={(e) => setTitle(e.target.value)}
                                placeholder="제목 입력"
                            />
                            <textarea
                                value={content}
                                onChange={(e) => setContent(e.target.value)}
                                placeholder="내용 입력"
                            ></textarea>

                            <button onClick={fetchSummary} className="btn-summary">
                                GPT 내용 요약
                            </button>
                            {summary && (
                                <div>
                                    <p>{summary}</p>
                                    <button onClick={updateTodoContent} className="btn-update">
                                        변경하기
                                    </button>
                                </div>
                            )}
                            <button onClick={onAddOrUpdate} className="btn-save">
                                저장
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default ToDoList;
