import React from "react";

const Editor = ({
    editorOpen,
    closeEditor,
    title,
    setTitle,
    content,
    setContent,
    onAddOrUpdate,
}) => {
    if (!editorOpen) return null;

    return (
        <div>
            <input
                type="text"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
            />
            <textarea
                value={content}
                onChange={(e) => setContent(e.target.value)}
            ></textarea>
            <button onClick={onAddOrUpdate}>저장</button>
            <button onClick={closeEditor}>닫기</button>
        </div>
    );
};

export default Editor;
