import React, { useState } from "react";
import { chat } from './openai';
import { useNavigate } from 'react-router-dom';


function ToDoList() {


    const navigate = useNavigate();

    const [folders, setFolders] = useState([
        { id: 1, name: "개인" },
        { id: 2, name: "업무" },
        { id: 3, name: "휴지통" },
    ]);

    const [favorites, setFavorites] = useState([]);
    const [selectedFolder, setSelectedFolder] = useState(1);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingTodo, setEditingTodo] = useState(null);
    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [newFolderName, setNewFolderName] = useState("");
    const [query, setQuery] = useState("");
    const [summary, setSummary] = useState("");
    const [sortOrder, setSortOrder] = useState("latest");
    const [filterDate, setFilterDate] = useState("");


    // State to hold the logged-in user ID
    const [loggedInUserId, setLoggedInUserId] = useState(null);

    useEffect(() => {
        if (loggedInUserId !== null) {
            // Fetch todos for the logged-in user
            axios.get(`http://localhost:3000/todos?userId=${loggedInUserId}`)
                .then(response => {
                    const userTodos = response.data;
                    const todosByFolder = {
                        1: [], // Personal
                        2: [], // Work
                        3: []  // Trash
                    };

                    userTodos.forEach(todo => {
                        if (todo.folderId) {
                            todosByFolder[todo.folderId].push(todo);
                        }
                    });
                    setTodos(todosByFolder);
                })
                .catch(error => console.error("Error fetching todos:", error));
        }
    }, [loggedInUserId]);

    const login = (userId, password) => {
        axios.get(`http://localhost:3000/users?userId=${userId}&password=${password}`)
            .then(response => {
                if (response.data.length > 0) {
                    setLoggedInUserId(response.data[0].id);
                    navigate("/todo"); // Navigate to the todo page after login
                } else {
                    alert("Invalid login credentials.");
                }
            })
            .catch(error => console.error("Error during login:", error));
    };

    const getCurrentDate = () => {
        const now = new Date();
        const year = now.getFullYear();
        const month = String(now.getMonth() + 1).padStart(2, "0");
        const date = String(now.getDate()).padStart(2, "0");
        const hours = String(now.getHours()).padStart(2, "0");
        const minutes = String(now.getMinutes()).padStart(2, "0");
        const seconds = String(now.getSeconds()).padStart(2, "0");

        return `${year}-${month}-${date} ${hours}:${minutes}:${seconds}`;
    };

    const onAddOrUpdate = () => {
        if (title.trim() === "" || content.trim() === "") {
            alert("제목과 내용을 입력하세요!");
            return;
        }
        const folderTodos = todos[selectedFolder] || [];
        if (editingTodo) {
            const updatedTodos = folderTodos.map((todo) =>
                todo.id === editingTodo.id ? { ...todo, title, content, date: getCurrentDate() } : todo
            );
            setTodos({ ...todos, [selectedFolder]: updatedTodos });
        } else {
            const id = folderTodos.length > 0 ? folderTodos[folderTodos.length - 1].id + 1 : 1;
            const updatedTodos = [...folderTodos, { id, title, content, date: getCurrentDate(), folderId: selectedFolder }];
            setTodos({ ...todos, [selectedFolder]: updatedTodos });
        }
        closeModal();
    };

    const closeModal = () => {
        setIsModalOpen(false);
        setEditingTodo(null);
        setTitle("");
        setContent("");
        setSummary("");
        setFilterDate("");
    };

    return (
        <div style={{ display: "flex", height: "100vh" }}>
            {/* 폴더 리스트 */}
            <div
                style={{
                    width: "300px",
                    borderRight: "2px solid #ccc",
                    padding: 10,
                    boxSizing: "border-box",
                }}
            >
                <h2>ToDoList</h2>
                <button
                    onClick={() => navigate("/login")}

                >
                    로그인
                </button>

                <button
                    onClick={() => openModal()}
                >
                    메모 쓰기
                </button>

                <ul style={{ listStyle: "none", padding: 0 }}>
                    {folders.map((folder) => (
                        <li
                            key={folder.id}
                            onClick={() => setSelectedFolder(folder.id)}
                            style={{
                                cursor: "pointer",
                                fontWeight: selectedFolder === folder.id ? "bold" : "normal",
                                backgroundColor: selectedFolder === folder.id ? "#e0e0e0" : "transparent",
                                padding: "5px",
                                borderRadius: "4px"
                            }}
                        >
                            {folder.name}
                        </li>
                    ))}
                </ul>

                <div>
                    <input
                        type="text"
                        value={newFolderName}
                        onChange={(e) => setNewFolderName(e.target.value)}
                        placeholder="새 폴더 이름"
                    />
                    <button
                        onClick={() => {
                            if (newFolderName.trim()) {
                                const id = folders.length > 0 ? folders[folders.length - 1].id + 1 : 1;
                                setFolders([...folders, { id, name: newFolderName.trim() }]);
                                setNewFolderName("");
                            }
                        }}

                    >
                        폴더 추가
                    </button>
                </div>
                <h3>{folders.find((folder) => folder.id === selectedFolder)?.name} 폴더 즐겨찾기</h3>
                {favorites.map((id) => {
                    const note = todos[selectedFolder]?.find((todo) => todo.id === id);
                    return note ? (
                        <div key={id} onClick={() => openModal(note)}>
                            {note.title}
                        </div>
                    ) : null;
                })}
            </div>

            {/* 메모 영역 */}
            <div style={{ flex: 1, padding: 20 }}>
                <h2>{folders.find((folder) => folder.id === selectedFolder)?.name}</h2>
                <input
                    type="text"
                    placeholder="검색 (제목/내용)"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    style={{
                        width: "100%",
                        padding: 10,
                        marginBottom: 20,
                        boxSizing: "border-box",
                    }}
                />
                <input
                    type="date"
                    value={filterDate}
                    onChange={(e) => setFilterDate(e.target.value)}

                />
                <button onClick={() => setSortOrder(sortOrder === "latest" ? "oldest" : "latest")}
                    style={{
                        cursor: "pointer"
                    }}>
                    {sortOrder === "latest" ? "최신순" : "오래된 순"}
                </button>
                <div
                    style={{
                        display: "grid",
                        gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
                        gap: 20,
                    }}
                >
                    {filteredTodos.map((todo) => (
                        <div
                            key={todo.id}
                            style={{
                                border: "1px solid #ccc",
                                borderRadius: 10,
                                padding: 10,
                                backgroundColor: "white",
                            }}
                        >
                            <h3
                                onClick={() => openModal(todo)}
                                style={{
                                    cursor: "pointer"
                                }}>
                                {todo.title}
                            </h3>
                            <p style={{ fontSize: 14, color: "#555" }}>{todo.content}</p>
                            <p>{todo.date}</p>

                            <button onClick={() => toggleFavorite(todo.id)}
                                style={{
                                    cursor: "pointer"
                                }}>
                                {favorites.includes(todo.id) ? "★" : "☆"}
                            </button>
                            {selectedFolder !== 3 && (
                                <button onClick={() => onDelete(todo.id)}
                                    style={{
                                        cursor: "pointer"
                                    }}>
                                    삭제
                                </button>)}
                            {selectedFolder === 3 && (
                                <button
                                    onClick={() => restoreTodo(todo.id)}
                                    style={{
                                        cursor: "pointer"
                                    }}>
                                    복원
                                </button>
                            )}
                            {selectedFolder === 3 && (
                                <button onClick={() => onRealDelete(todo.id)}
                                    style={{
                                        cursor: "pointer"
                                    }}>
                                    완전 삭제
                                </button>
                            )}
                        </div>
                    ))}
                </div>
            </div>

            {isModalOpen && (
                <div
                    style={{
                        position: "fixed",
                        width: "100%",
                        height: "100%",
                        backgroundColor: "rgba(0, 0, 0, 0.05)",
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                    }}>
                    <div
                        style={{
                            backgroundColor: "white",
                            width: "50%",
                            height: "50%",
                            borderRadius: 10,
                        }}>
                        <button
                            onClick={closeModal}
                            style={{
                                cursor: "pointer"
                            }}>
                            ✕
                        </button>
                        <div style={{ padding: 10 }}>
                            <input
                                type="text"
                                value={title}
                                onChange={(e) => setTitle(e.target.value)}
                                placeholder="제목 입력"
                                style={{
                                    width: "100%",
                                    padding: 10,
                                    fontSize: 16,
                                    boxSizing: "border-box",
                                    marginBottom: 10,
                                }}
                            />
                            <textarea
                                value={content}
                                onChange={(e) => setContent(e.target.value)}
                                placeholder="내용 입력"
                                style={{
                                    width: "100%",
                                    height: 200,
                                    padding: 10,
                                    fontSize: 16,
                                    boxSizing: "border-box",
                                }}
                            ></textarea>

                            <button
                                onClick={fetchSummary}
                                style={{
                                    cursor: "pointer"
                                }}>
                                GPT 내용 요약
                            </button>
                            {summary && (
                                <div>
                                    <p>{summary}</p>
                                    <button
                                        onClick={updateTodoContent}
                                        style={{
                                            cursor: "pointer"
                                        }}>
                                        변경하기
                                    </button>
                                </div>
                            )}
                            <button
                                onClick={onAddOrUpdate}
                                style={{
                                    cursor: "pointer"
                                }}>
                                저장
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default ToDoList;
