import React, { useState } from "react";
import { chat } from './openai';
import { useNavigate } from 'react-router-dom';


function ToDoList() {
    const navigate = useNavigate();
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const handleLogin = async () => {
        if (isLoggedIn) {
            setIsLoggedIn(false); // 로그아웃 처리
            return;
        }

        try {
            const response = await fetch("http://localhost:3000/login", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ username: "test", password: "password" }), // 테스트용 데이터
            });

            if (response.ok) {
                setIsLoggedIn(true); // 로그인 성공
            } else {
                alert("로그인 실패! 아이디와 비밀번호를 확인하세요.");
            }
        } catch (error) {
            console.error("로그인 요청 중 오류 발생:", error);
        }
    };


    const [folders, setFolders] = useState([
        { id: 1, name: "개인" },
        { id: 2, name: "업무" },
        { id: 3, name: "휴지통" },
    ]);
    const [todos, setTodos] = useState({
        1: [
            { id: 1, title: "운동하기", content: "매일 아침 30분 운동", date: "2024-11-09" },
            { id: 2, title: "책 읽기", content: "소설 한 권 읽기", date: "2024-11-30" },
        ],
        2: [
            { id: 1, title: "프로젝트 보고서 작성", content: "최종 보고서 작성 및 검토", date: "2024-12-01" },
            { id: 2, title: "팀 회의 준비", content: "발표 자료 준비 및 회의록 작성", date: "2024-12-02" },
        ],
        3: []
    });
    const [favorites, setFavorites] = useState([]);
    const [selectedFolder, setSelectedFolder] = useState(1);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingTodo, setEditingTodo] = useState(null);
    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [newFolderName, setNewFolderName] = useState("");
    const [query, setQuery] = useState("");
    const [summary, setSummary] = useState("");
    const [sortOrder, setSortOrder] = useState("latest");
    const [filterDate, setFilterDate] = useState("");

    const getCurrentDate = () => {
        const now = new Date();
        const year = now.getFullYear();
        const month = String(now.getMonth() + 1).padStart(2, "0");
        const date = String(now.getDate()).padStart(2, "0");
        const hours = String(now.getHours()).padStart(2, "0");
        const minutes = String(now.getMinutes()).padStart(2, "0");
        const seconds = String(now.getSeconds()).padStart(2, "0");

        return `${year}-${month}-${date} ${hours}:${minutes}:${seconds}`;
    };


    const onAddOrUpdate = () => {
        if (title.trim() === "" || content.trim() === "") {
            alert("제목과 내용을 입력하세요!");
            return;
        }
        const folderTodos = todos[selectedFolder] || [];
        if (editingTodo) {
            const updatedTodos = folderTodos.map((todo) =>
                todo.id === editingTodo.id ? { ...todo, title, content, filterDate } : todo
            );
            setTodos({ ...todos, [selectedFolder]: updatedTodos });
        } else {
            const id = folderTodos.length > 0 ? folderTodos[folderTodos.length - 1].id + 1 : 1;
            const updatedTodos = [...folderTodos, { id, title, content, date: getCurrentDate() }];
            setTodos({ ...todos, [selectedFolder]: updatedTodos });
        }
        closeModal();
    };


    const onDelete = (id) => {
        const folderTodos = todos[selectedFolder];
        const updatedTodos = folderTodos.filter((todo) => todo.id !== id);
        const todoToDelete = folderTodos.find((todo) => todo.id === id);
        setTodos({ ...todos, [selectedFolder]: updatedTodos });
        if (todoToDelete) {
            setTodos(prevState => ({
                ...prevState,
                3: [...prevState[3], todoToDelete],
            }));
        }

    };
    const onRealDelete = (id) => {
        const folderTodos = todos[selectedFolder];
        const updatedTodos = folderTodos.filter((todo) => todo.id !== id);
        setTodos({ ...todos, [selectedFolder]: updatedTodos });
    };

    const TRASH_FOLDER_ID = 3;

    const restoreTodo = (id) => {
        const trashTodos = todos[TRASH_FOLDER_ID];
        const todoToRestore = trashTodos.find((todo) => todo.id === id);

        if (todoToRestore) {
            const originalFolder = todoToRestore.previousFolder || 1;
            setTodos((prevState) => {
                const updatedTrashTodos = trashTodos.filter((todo) => todo.id !== id);
                const updatedOriginalFolderTodos = [
                    ...prevState[originalFolder],
                    { ...todoToRestore, date: filterDate },
                ];
                return {
                    ...prevState,
                    [TRASH_FOLDER_ID]: updatedTrashTodos,
                    [originalFolder]: updatedOriginalFolderTodos,
                };
            });
        }
    };

    const toggleFavorite = (todoId) => {
        setFavorites((prev) =>
            prev.includes(todoId)
                ? prev.filter((id) => id !== todoId)
                : [...prev, todoId]
        );
    };

    const openModal = (todo = null) => {
        setIsModalOpen(true);
        if (todo) {
            setEditingTodo(todo);
            setTitle(todo.title);
            setContent(todo.content);
            setFilterDate(todo.date);
        } else {
            setEditingTodo(null);
            setTitle("");
            setContent("");
            setFilterDate("");
        }
    };

    const closeModal = () => {
        setIsModalOpen(false);
        setEditingTodo(null);
        setTitle("");
        setContent("");
        setSummary("");
        setFilterDate("");
    };

    const sortTodos = (todos) => {
        return todos.sort((a, b) =>
            sortOrder === "latest"
                ? new Date(b.date) - new Date(a.date)
                : new Date(a.date) - new Date(b.date)
        );
    };

    const filteredTodos = sortTodos(
        (todos[selectedFolder] || []).filter(
            (todo) =>
                (!filterDate || todo.date === filterDate) &&
                (todo.title.toLowerCase().includes(query.toLowerCase()) ||
                    todo.content.toLowerCase().includes(query.toLowerCase()))
        )
    );

    const fetchSummary = () => {
        const prompt = `다음은 사용자가 작성한 TodoList입니다. 내용을 간결하고, 가독성 있게 정리해주세요.
        ${content}`;

        chat(prompt, (response) => setSummary(response));
    };

    const updateTodoContent = () => {
        setContent(summary);
    };

    return (
        <div style={{ display: "flex", height: "100vh" }}>
            {/* 폴더 리스트 */}
            <div
                style={{
                    width: "300px",
                    borderRight: "2px solid #ccc",
                    padding: 10,
                    boxSizing: "border-box",
                }}
            >
                <h2>ToDoList</h2>
                <button
                    onClick={handleLogin}
                    style={{
                        width: "100%",
                        padding: 10,
                        backgroundColor: isLoggedIn ? "#4caf50" : "#f44336",
                        fontSize: 16,
                        fontWeight: "bold",
                        color: "white",
                        border: "none",
                        borderRadius: 5,
                        marginTop: 10,
                        cursor: "pointer",
                    }}
                >
                    {isLoggedIn ? "로그아웃" : "로그인"}
                </button>

                <button
                    onClick={() => openModal()}
                    style={{
                        width: "100%",
                        padding: 10,
                        backgroundColor: "#2196f3",
                        fontSize: 16,
                        fontWeight: "bold",
                        color: "white",
                        border: "none",
                        borderRadius: 5,
                        cursor: "pointer",
                    }}
                >
                    메모 쓰기
                </button>

                <ul style={{ listStyle: "none", padding: 0 }}>
                    {folders.map((folder) => (
                        <li
                            key={folder.id}
                            style={{
                                padding: 10,
                                marginBottom: 5,
                                cursor: "pointer",
                                backgroundColor: selectedFolder === folder.id ? "#ddd" : "transparent",
                            }}
                            onClick={() => setSelectedFolder(folder.id)}
                        >
                            {folder.name}
                        </li>
                    ))}
                </ul>
                <div>
                    <input
                        type="text"
                        value={newFolderName}
                        onChange={(e) => setNewFolderName(e.target.value)}
                        placeholder="새 폴더 이름"
                        style={{
                            width: "100%",
                            padding: 5,
                            marginBottom: 5,
                            boxSizing: "border-box",
                        }}
                    />
                    <button
                        onClick={() => {
                            if (newFolderName.trim()) {
                                const id = folders.length > 0 ? folders[folders.length - 1].id + 1 : 1;
                                setFolders([...folders, { id, name: newFolderName.trim() }]);
                                setNewFolderName("");
                            }
                        }}
                        style={{
                            width: "100%",
                            padding: 10,
                            backgroundColor: "#4caf50",
                            color: "white",
                            border: "none",
                            cursor: "pointer",
                        }}
                    >
                        폴더 추가
                    </button>
                </div>
                <h3>{folders.find((folder) => folder.id === selectedFolder)?.name} 폴더 즐겨찾기</h3>
                {favorites.map((id) => {
                    const note = todos[selectedFolder]?.find((todo) => todo.id === id);
                    return note ? (
                        <div key={id} onClick={() => openModal(note)}>
                            {note.title}
                        </div>
                    ) : null;
                })}
            </div>

            {/* 메모 영역 */}
            <div style={{ flex: 1, padding: 20 }}>
                <h2>{folders.find((folder) => folder.id === selectedFolder)?.name}</h2>
                <input
                    type="text"
                    placeholder="검색 (제목/내용)"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    style={{
                        width: "100%",
                        padding: 10,
                        marginBottom: 20,
                        boxSizing: "border-box",
                    }}
                />
                <input
                    type="date"
                    value={filterDate}
                    onChange={(e) => setFilterDate(e.target.value)}
                    style={{
                        padding: 5,
                        borderRadius: 5,
                        width: 150,
                        marginBottom: 5,
                        marginRight: 5,
                        fontSize: "14px",
                    }}
                />
                <button onClick={() => setSortOrder(sortOrder === "latest" ? "oldest" : "latest")}
                    style={{
                        padding: 5,
                        borderRadius: 5,
                        color: "black",
                        fontSize: "14px",
                        cursor: "pointer",
                    }}>
                    {sortOrder === "latest" ? "최신순" : "오래된 순"}
                </button>
                <div
                    style={{
                        display: "grid",
                        gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
                        gap: 20,
                    }}
                >
                    {filteredTodos.map((todo) => (
                        <div
                            key={todo.id}
                            style={{
                                border: "1px solid #ccc",
                                borderRadius: 10,
                                padding: 10,
                                backgroundColor: "white",
                            }}
                        >
                            <h3
                                onClick={() => openModal(todo)}
                                style={{
                                    cursor: "pointer",
                                    fontSize: 18,
                                    margin: 0
                                }}
                            >
                                {todo.title}
                            </h3>
                            <p style={{ fontSize: 14, color: "#555" }}>{todo.content}</p>
                            <p>{todo.date}</p>

                            <button
                                onClick={() => toggleFavorite(todo.id)}
                                style={{
                                    backgroundColor: favorites.includes(todo.id) ? "#ffcc00" : "#ccc",
                                    color: "black",
                                    border: "none",
                                    borderRadius: 5,
                                    padding: "5px 10px",
                                    cursor: "pointer",
                                    fontSize: 12,
                                }}
                            >
                                {favorites.includes(todo.id) ? "★" : "☆"}
                            </button>
                            {selectedFolder !== 3 && (<button
                                onClick={() => onDelete(todo.id)}
                                style={{
                                    backgroundColor: "#ff4d4d",
                                    color: "white",
                                    border: "none",
                                    borderRadius: 5,
                                    padding: "5px 10px",
                                    cursor: "pointer",
                                    fontSize: 12,
                                    float: "right",
                                }}
                            >
                                삭제
                            </button>)}
                            {selectedFolder === 3 && (
                                <button
                                    onClick={() => restoreTodo(todo.id)}
                                    style={{
                                        backgroundColor: "#4caf50",
                                        color: "white",
                                        border: "none",
                                        borderRadius: 5,
                                        padding: "5px 10px",
                                        cursor: "pointer",
                                        fontSize: 12,
                                    }}
                                >
                                    복원
                                </button>
                            )}
                            {selectedFolder === 3 && (
                                <button
                                    onClick={() => onRealDelete(todo.id)}
                                    style={{
                                        backgroundColor: "#ff4d4d",
                                        color: "white",
                                        border: "none",
                                        borderRadius: 5,
                                        padding: "5px 10px",
                                        cursor: "pointer",
                                        fontSize: 12,
                                        float: "right",
                                    }}
                                >
                                    완전 삭제
                                </button>
                            )}
                        </div>
                    ))}
                </div>
            </div>

            {isModalOpen && (
                <div
                    style={{
                        position: "fixed",
                        width: "100%",
                        height: "100%",
                        backgroundColor: "rgba(0, 0, 0, 0.05)",
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                    }}
                >
                    <div
                        style={{
                            backgroundColor: "white",
                            width: "80%",
                            height: "80%",
                            borderRadius: 10,
                        }}
                    >
                        <button
                            onClick={closeModal}
                            style={{
                                padding: 5,
                                marginLeft: 10,
                                fontSize: 30,
                                border: 'none',
                                cursor: 'pointer',
                                background: 'white'

                            }}
                        >
                            ✕
                        </button>
                        <div style={{ padding: 10 }}>
                            <input
                                type="text"
                                value={title}
                                onChange={(e) => setTitle(e.target.value)}
                                placeholder="제목 입력"
                                style={{
                                    width: "100%",
                                    padding: 10,
                                    fontSize: 16,
                                    boxSizing: "border-box",
                                    marginBottom: 10,
                                }}
                            />
                            <textarea
                                value={content}
                                onChange={(e) => setContent(e.target.value)}
                                placeholder="내용 입력"
                                style={{
                                    width: "100%",
                                    height: 200,
                                    padding: 10,
                                    fontSize: 16,
                                    boxSizing: "border-box",
                                }}
                            ></textarea>

                            <button
                                onClick={fetchSummary}
                                style={{
                                    width: "100%",
                                    padding: 10,
                                    backgroundColor: "#FF0000",
                                    fontSize: 16,
                                    fontWeight: "bold",
                                    color: "white",
                                    border: "none",
                                    borderRadius: 5,
                                    cursor: "pointer",
                                }}
                            >
                                GPT 내용 요약
                            </button>
                            {summary && (
                                <div>
                                    <p>{summary}</p>
                                    <button
                                        onClick={updateTodoContent}
                                        style={{
                                            width: "100%",
                                            padding: 10,
                                            backgroundColor: "#3498db",
                                            fontSize: 16,
                                            fontWeight: "bold",
                                            color: "white",
                                            border: "none",
                                            borderRadius: 5,
                                            cursor: "pointer",
                                        }}
                                    >
                                        변경하기
                                    </button>
                                </div>
                            )}
                            <button
                                onClick={onAddOrUpdate}
                                style={{
                                    marginTop: 10,
                                    width: "100%",
                                    padding: 10,
                                    backgroundColor: "#4caf50",
                                    fontSize: 16,
                                    fontWeight: "bold",
                                    color: "white",
                                    border: "none",
                                    borderRadius: 5,
                                    cursor: "pointer",
                                }}
                            >
                                저장
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default ToDoList;
