import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
    const [formData, setFormData] = useState({ userId: '', password: '' });
    const [errors, setErrors] = useState({});
    const navigate = useNavigate();

    const [userId, setUserId] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");

    const handleLogin = async () => {
        try {
            const response = await fetch("http://localhost:8000/users");
            const users = await response.json();

            const user = users.find(
                (u) => u.userId === userId && u.password === password
            );

            if (user) {
                // 로그인 성공 시 sessionStorage에 사용자 ID 저장
                sessionStorage.setItem("loggedInUserId", user.id);
                navigate("/todolist"); // ToDoList로 이동
            } else {
                setError("아이디 또는 비밀번호가 잘못되었습니다.");
            }
        } catch (error) {
            console.error("로그인 실패:", error);
            setError("서버에 연결할 수 없습니다. 다시 시도해주세요.");
        }
    };
    return (
        <div style={{ marginTop: '5rem', textAlign: 'center' }}>
            <h4>로그인</h4>
            <form onSubmit={handleSubmit} style={{ display: 'inline-block', textAlign: 'left' }}>
                <div>
                    <label htmlFor="userId">아이디:</label>
                    <input
                        type="text"
                        id="userId"
                        name="userId"
                        value={formData.userId}
                        onChange={handleChange}
                        style={{ display: 'block', marginBottom: '0.5rem' }}
                    />
                    {errors.userId && <span style={{ color: 'red' }}>{errors.userId}</span>}
                </div>

                <div>
                    <label htmlFor="password">비밀번호:</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        style={{ display: 'block', marginBottom: '0.5rem' }}
                    />
                    {errors.password && <span style={{ color: 'red' }}>{errors.password}</span>}
                </div>

                <button type="submit" style={{ marginTop: '1rem' }}>로그인</button>
                <button
                    type="button"
                    onClick={() => navigate('/join')}
                    style={{ marginLeft: '1rem' }}
                >
                    회원가입
                </button>
            </form>
        </div>
    );
}

export default Login;
