import React, { useState } from 'react';
import { chat } from './openai';

export default function TodoListRefiner() {
  const [todoList, setTodoList] = useState('');
  const [result, setResult] = useState('');

  const refineTodoList = () => {
    const prompt = `다음은 사용자가 작성한 TodoList입니다. 내용을 간결하고, 가독성 있게 정리해주세요.
TodoList:
${todoList}`;
    chat(prompt, (response) => setResult(response));
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>TodoList Refiner</h1>
      <div style={{ marginBottom: '20px' }}>
        <label htmlFor="todoInput" style={{ display: 'block', marginBottom: '8px' }}>TodoList 입력:</label>
        <textarea
          id="todoInput"
          style={{ width: '100%', height: '100px', fontSize: '16px', borderWidth: '1px', padding: '8px' }}
          placeholder="할 일을 여기에 입력하세요 (각 항목은 줄바꿈으로 구분)"
          value={todoList}
          onChange={(e) => setTodoList(e.target.value)}
        />
      </div>

      <button
        style={{ padding: '10px 20px', fontSize: '16px', cursor: 'pointer', backgroundColor: '#007BFF', color: 'white', border: 'none', borderRadius: '4px' }}
        onClick={refineTodoList}
      >
        정리하기
      </button>

      <div style={{ marginTop: '20px' }}>
        <h2>정리된 TodoList:</h2>
        <div style={{ whiteSpace: 'pre-wrap', padding: '10px', border: '1px solid #ddd', borderRadius: '4px', backgroundColor: '#f9f9f9' }}>
          {result || '결과가 여기에 표시됩니다.'}
        </div>
      </div>
    </div>
  );
}
