import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
    const [formData, setFormData] = useState({ userId: '', password: '' });
    const [rememberUserId, setRememberUserId] = useState(false);
    const [errors, setErrors] = useState({});
    const navigate = useNavigate();

    useEffect(() => {
        const savedUserId = localStorage.getItem('userId');
        if (savedUserId) {
            setFormData((prev) => ({ ...prev, userId: savedUserId }));
            setRememberUserId(true);
        }
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
    };

    const handleCheckboxChange = () => {
        setRememberUserId((prev) => !prev);
        if (!rememberUserId) {
            localStorage.setItem('userId', formData.userId);
        } else {
            localStorage.removeItem('userId');
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const validationErrors = {};
        if (!formData.userId) validationErrors.userId = "아이디를 입력하세요.";
        if (!formData.password) validationErrors.password = "비밀번호를 입력하세요.";

        if (Object.keys(validationErrors).length === 0) {
            axios.get('http://localhost:8000/users')
                .then((result) => {
                    const user = result.data.find(user => user.userId === formData.userId);
                    if (user && user.password === formData.password) {
                        alert("로그인 되었습니다.");
                        navigate('/');
                    } else {
                        setErrors({
                            userId: user ? undefined : "아이디가 틀렸습니다.",
                            password: user ? "비밀번호가 틀렸습니다." : undefined
                        });
                    }
                })
                .catch(err => console.error(err));
        } else {
            setErrors(validationErrors);
        }
    };

    return (
        <div style={{ margin: '5rem auto', width: '300px', textAlign: 'center' }}>
            <h4>로그인</h4>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="userId">아이디</label>
                    <input
                        type="text"
                        id="userId"
                        name="userId"
                        value={formData.userId}
                        onChange={handleChange}
                        style={{ width: '100%', marginBottom: '10px' }}
                    />
                    {errors.userId && <div style={{ color: 'red' }}>{errors.userId}</div>}
                </div>
                <div>
                    <label htmlFor="password">비밀번호</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        style={{ width: '100%', marginBottom: '10px' }}
                    />
                    {errors.password && <div style={{ color: 'red' }}>{errors.password}</div>}
                </div>
                <div>
                    <input
                        type="checkbox"
                        id="rememberUserId"
                        checked={rememberUserId}
                        onChange={handleCheckboxChange}
                    />
                    <label htmlFor="rememberUserId">아이디 저장</label>
                </div>
                <button type="submit" style={{ marginTop: '10px' }}>로그인</button>
            </form>
            <button onClick={() => navigate('/join')} style={{ marginTop: '10px' }}>회원가입</button>
        </div>
    );
}

export default Login;
