import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
    const [formData, setFormData] = useState({
        userId: '',
        password: ''
    });
    const [rememberuserId, setRememberuserId] = useState(false);
    const [errors, setErrors] = useState({});
    const navigate = useNavigate();

    // Load saved userId from local storage on component mount
    useEffect(() => {
        const saveduserId = localStorage.getItem('userId');
        if (saveduserId) {
            setFormData({ ...formData, userId: saveduserId });
            setRememberuserId(true);
        }
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({
            ...prev,
            [name]: value
        }));
    };

    const handleCheckboxChange = () => {
        setRememberuserId((prev) => !prev);
        if (!rememberuserId) {
            localStorage.setItem('userId', formData.userId);
        } else {
            localStorage.removeItem('userId');
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        let validationErrors = {};
        let isValid = true;

        // Validation logic
        if (!formData.userId) {
            isValid = false;
            validationErrors.userId = "아이디를 입력하세요.";
        }
        if (!formData.password) {
            isValid = false;
            validationErrors.password = "비밀번호를 입력하세요.";
        }

        if (isValid) {
            axios.get('http://localhost:8000/users')
                .then((result) => {
                    const user = result.data.find(user => user.userId === formData.userId);
                    if (user) {
                        if (user.password === formData.password) {
                            alert("로그인 되었습니다.");
                            navigate('/');
                        } else {
                            isValid = false;
                            validationErrors.password = "비밀번호가 틀렸습니다.";
                        }
                    } else {
                        isValid = false;
                        validationErrors.userId = "아이디가 틀렸습니다.";
                    }
                    setErrors(validationErrors);
                })
                .catch(err => console.error(err));
        } else {
            setErrors(validationErrors);
        }
    };

    const handleBack = () => {
        navigate('/');
    };

    const handleSignUp = () => {
        navigate('/join');
    };

    return (
        <div style={{ marginTop: '5rem', display: 'flex', justifyContent: 'center' }}>
            <div style={{ width: '30rem', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', borderRadius: '8px', backgroundColor: '#fff' }}>
                <div style={{ padding: '2rem' }}>
                    <h4 style={{ textAlign: 'center', marginBottom: '1.5rem', fontSize: '1.5rem', color: '#333' }}>로그인</h4>
                    <form onSubmit={handleSubmit}>
                        <div>
                            <label htmlFor="userId" style={{ fontWeight: 'bold', marginBottom: '0.5rem', display: 'block', color: '#555' }}>아이디:</label>
                            <input
                                type="text"
                                id="userId"
                                name="userId"
                                value={formData.userId}
                                onChange={handleChange}
                                required
                                style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem', border: '1px solid #ccc', borderRadius: '4px' }}
                            />
                            {errors.userId && <div style={{ color: 'red', marginBottom: '1rem' }}>{errors.userId}</div>}
                        </div>

                        <div>
                            <label htmlFor="password" style={{ fontWeight: 'bold', marginBottom: '0.5rem', display: 'block', color: '#555' }}>비밀번호:</label>
                            <input
                                type="password"
                                id="password"
                                name="password"
                                value={formData.password}
                                onChange={handleChange}
                                required
                                style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem', border: '1px solid #ccc', borderRadius: '4px' }}
                            />
                            {errors.password && <div style={{ color: 'red', marginBottom: '1rem' }}>{errors.password}</div>}
                        </div>

                        <div style={{ marginBottom: '1rem' }}>
                            <input
                                type="checkbox"
                                id="rememberuserId"
                                checked={rememberuserId}
                                onChange={handleCheckboxChange}
                            />
                            <label htmlFor="rememberuserId">아이디 저장하기</label>
                        </div>

                        <hr />
                        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                            <button
                                type="submit"
                                style={{ backgroundColor: '#28a745', color: '#fff', border: 'none', padding: '0.5rem 1rem', borderRadius: '4px', cursor: 'pointer', fontSize: '1rem' }}
                                onMouseOver={(e) => e.target.style.backgroundColor = '#218838'}
                                onMouseOut={(e) => e.target.style.backgroundColor = '#28a745'}
                            >
                                로그인
                            </button>
                            <button
                                type="button"
                                onClick={handleBack}
                                style={{ backgroundColor: '#6c757d', color: '#fff', padding: '0.5rem 1rem', borderRadius: '4px', cursor: 'pointer', fontSize: '1rem' }}
                                onMouseOver={(e) => e.target.style.backgroundColor = '#5a6268'}
                                onMouseOut={(e) => e.target.style.backgroundColor = '#6c757d'}
                            >
                                뒤로가기
                            </button>
                        </div>
                    </form>
                    <div style={{ textAlign: 'center', marginTop: '1rem' }}>
                        <button
                            type="button"
                            onClick={handleSignUp}
                            style={{ backgroundColor: '#007bff', color: '#fff', border: 'none', padding: '0.25rem 0.5rem', borderRadius: '4px', cursor: 'pointer', fontSize: '0.875rem' }}
                            onMouseOver={(e) => e.target.style.backgroundColor = '#0056b3'}
                            onMouseOut={(e) => e.target.style.backgroundColor = '#007bff'}
                        >
                            회원가입
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Login;
