import React from 'react';

function TodoEditor({ editorOpen, closeEditor, title, setTitle, content, setContent, fetchSummary, summary, updateTodoContent, onAddOrUpdate }) {
    return (
        editorOpen && (
            <div
                style={{
                    position: "fixed",
                    width: "100%",
                    height: "100%",
                    backgroundColor: "rgba(0, 0, 0, 0.05)",
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                }}>
                <div
                    style={{
                        backgroundColor: "white",
                        width: "50%",
                        height: "50%",
                        borderRadius: 10,
                    }}>
                    <button
                        onClick={closeEditor}>
                        ✕
                    </button>
                    <div style={{ padding: 10 }}>
                        <input
                            type="text"
                            value={title}
                            onChange={(e) => setTitle(e.target.value)}
                            placeholder="제목 입력"
                            style={{
                                width: "100%",
                                padding: 10,
                                fontSize: 16,
                                boxSizing: "border-box",
                                marginBottom: 10,
                            }}
                        />
                        <textarea
                            value={content}
                            onChange={(e) => setContent(e.target.value)}
                            placeholder="내용 입력"
                            style={{
                                width: "100%",
                                height: 200,
                                padding: 10,
                                fontSize: 16,
                                boxSizing: "border-box",
                            }}
                        ></textarea>

                        <button
                            onClick={fetchSummary}>
                            GPT 내용 요약
                        </button>
                        {summary && (
                            <div>
                                <p>{summary}</p>
                                <button
                                    onClick={updateTodoContent}>
                                    변경하기
                                </button>
                            </div>
                        )}
                        <button
                            onClick={onAddOrUpdate}>
                            저장
                        </button>
                    </div>
                </div>
            </div>
        )
    );
}

export default TodoEditor;
