import axios from 'axios';
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Join() {
    const [formData, setFormData] = useState({
        userId: '',
        password: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const [error, setErrors] = useState({})
    const [valid, setValid] = useState(true)
    const navigate = useNavigate()

    const handleSubmit = (e) => {
        e.preventDefault();
        let isvalid = true;
        let validationErrors = {}
        if (formData.userId === "" || formData.userId === null) {
            isvalid = false;
            validationErrors.userId = "아이디를 입력하세요."
        }
        if (formData.password === "" || formData.password === null) {
            isvalid = false;
            validationErrors.password = "비밀번호를 입력하세요."
        }
        setErrors(validationErrors)
        setValid(isvalid)

        if (Object.keys(validationErrors).length === 0) {
            alert("회원 가입이 완료되었습니다.")
        }


        axios.get('http://localhost:8000/users')
            .then(result => {
                result.date.map(user => {
                    if (user.userId === formData.userId) {
                        if (user.password === formData.password) {
                            alert("로그인 되었습니다.")
                            navigate('/')
                        } else {
                            isvalid = false;
                            validationErrors.password = "비밀번호가 틀렸습니다."
                        }
                    }
                    else if (formData.userId !== "") {
                        isvalid = false;
                        validationErrors.userId = "아이디가 틀렸습니다."
                    }
                })
                setErrors(validationErrors)
                setValid(isvalid)

                if (Object.keys(validationErrors).length === 0) {
                    axios.post('http://localhost:8000/users', formData)
                        .then(result => console.log(result))
                        .catch(err => console.log(err))
                }
            })





    };

    return (
        <div style={{ marginTop: '5rem', display: 'flex', justifyContent: 'center' }}>
            <div style={{ width: '30rem', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', borderRadius: '8px', overflow: 'hidden', backgroundColor: '#fff' }}>
                <div style={{ padding: '2rem' }}>
                    <h4 style={{ textAlign: 'center', marginBottom: '1.5rem', fontSize: '1.5rem', color: '#333' }}>회원 가입</h4>
                    <form onSubmit={handleSubmit}>
                        <div>
                            <label htmlFor="userId" style={{ fontWeight: 'bold', marginBottom: '0.5rem', display: 'block', color: '#555' }}>아이디:</label>
                            <input
                                type="text"
                                id="userId"
                                name="userId"
                                value={formData.userId}
                                onChange={handleChange}
                                required
                                style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem', border: '1px solid #ccc', borderRadius: '4px', boxSizing: 'border-box' }}
                            />
                        </div>

                        <div>
                            <label htmlFor="password" style={{ fontWeight: 'bold', marginBottom: '0.5rem', display: 'block', color: '#555' }}>비밀번호:</label>
                            <input
                                type="password"
                                id="password"
                                name="password"
                                value={formData.password}
                                onChange={handleChange}
                                required
                                style={{ width: '100%', padding: '0.5rem', marginBottom: '1rem', border: '1px solid #ccc', borderRadius: '4px', boxSizing: 'border-box' }}
                            />
                        </div>
                        <hr />
                        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                            <button
                                type="submit"
                                style={{ backgroundColor: '#28a745', color: '#fff', border: 'none', padding: '0.5rem 1rem', borderRadius: '4px', cursor: 'pointer', fontSize: '1rem' }}
                                onMouseOver={(e) => e.target.style.backgroundColor = '#218838'}
                                onMouseOut={(e) => e.target.style.backgroundColor = '#28a745'}
                            >
                                가입하기
                            </button>
                            <a
                                href="/products/all"
                                style={{ backgroundColor: '#6c757d', color: '#fff', padding: '0.5rem 1rem', borderRadius: '4px', textDecoration: 'none', display: 'inline-block', fontSize: '1rem' }}
                                onMouseOver={(e) => e.target.style.backgroundColor = '#5a6268'}
                                onMouseOut={(e) => e.target.style.backgroundColor = '#6c757d'}
                            >
                                뒤로가기
                            </a>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Join;
