import React, { useState } from "react";

const CombinedTodolist = () => {
    const [notes, setNotes] = useState([
        { id: 1, title: "메모1", content: "내용1" },
        { id: 2, title: "메모2", content: "내용2" },
        { id: 3, title: "메모3", content: "내용3" },
    ]);
    const [selectedNote, setSelectedNote] = useState(null);

    const [folders, setFolders] = useState([
        { id: 1, name: "개인" },
        { id: 2, name: "업무" },
    ]);
    const [todos, setTodos] = useState({
        1: [
            { id: 1, title: "운동하기", content: "매일 아침 30분 운동", check: false },
            { id: 2, title: "책 읽기", content: "소설 한 권 읽기", check: true },
        ],
        2: [
            { id: 1, title: "프로젝트 보고서 작성", content: "최종 보고서 작성 및 검토", check: false },
            { id: 2, title: "팀 회의 준비", content: "발표 자료 준비 및 회의록 작성", check: true },
        ],
    });
    const [selectedFolder, setSelectedFolder] = useState(1);

    const handleSelectNote = (note) => {
        setSelectedNote(note);
    };

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingTodo, setEditingTodo] = useState(null);
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');

    const onAddOrUpdate = () => {
        if (title.trim() === '' || content.trim() === '') {
            alert('제목과 내용을 입력하세요!');
            return;
        }
        const folderTodos = todos[selectedFolder] || [];
        if (editingTodo) {
            const updatedTodos = folderTodos.map((todo) =>
                todo.id === editingTodo.id ? { ...todo, title, content } : todo
            );
            setTodos({ ...todos, [selectedFolder]: updatedTodos });
        } else {
            const id = folderTodos.length > 0 ? folderTodos[folderTodos.length - 1].id + 1 : 1;
            const updatedTodos = [...folderTodos, { id, title, content, check: false }];
            setTodos({ ...todos, [selectedFolder]: updatedTodos });
        }
        closeModal();
    };

    const onDelete = (id) => {
        const folderTodos = todos[selectedFolder];
        const updatedTodos = folderTodos.filter((todo) => todo.id !== id);
        setTodos({ ...todos, [selectedFolder]: updatedTodos });
    };

    const openModal = (todo = null) => {
        setIsModalOpen(true);
        if (todo) {
            setEditingTodo(todo);
            setTitle(todo.title);
            setContent(todo.content);
        } else {
            setEditingTodo(null);
            setTitle('');
            setContent('');
        }
    };

    const closeModal = () => {
        setIsModalOpen(false);
        setEditingTodo(null);
        setTitle('');
        setContent('');
    };

    return (
        <div style={{ display: "flex", height: "100vh" }}>
            {/* Sidebar for folders */}
            <div
                style={{
                    width: "300px",
                    borderRight: "2px solid #ccc",
                    padding: 10,
                    boxSizing: "border-box",
                }}
            >
                <h2>폴더</h2>
                <button
                    onClick={() => openModal()}
                    style={{
                        width: "100%",
                        padding: 10,
                        backgroundColor: "#2196f3",
                        color: "white",
                        border: "none",
                        borderRadius: 5,
                        cursor: "pointer",
                    }}
                >
                    새 메모 추가
                </button>

                <ul style={{ listStyle: "none", padding: 0 }}>
                    {folders.map((folder) => (
                        <li
                            key={folder.id}
                            style={{
                                padding: 10,
                                marginBottom: 5,
                                cursor: "pointer",
                                backgroundColor: selectedFolder === folder.id ? "#ddd" : "transparent",
                            }}
                            onClick={() => setSelectedFolder(folder.id)}
                        >
                            {folder.name}
                        </li>
                    ))}
                </ul>
            </div>

            {/* Main content */}
            <div style={{ flex: 1, padding: 20 }}>
                <h2>{folders.find((folder) => folder.id === selectedFolder)?.name} To-Do List</h2>
                <div style={{ display: "grid", gap: 20 }}>
                    {(todos[selectedFolder] || []).map((todo) => (
                        <div
                            key={todo.id}
                            style={{
                                border: "1px solid #ccc",
                                borderRadius: 10,
                                padding: 10,
                                backgroundColor: "white",
                            }}
                        >
                            <h3
                                onClick={() => openModal(todo)}
                                style={{ cursor: "pointer" }}
                            >
                                {todo.title}
                            </h3>
                            <p>{todo.content}</p>
                            <button
                                onClick={() => onDelete(todo.id)}
                                style={{
                                    backgroundColor: "#ff4d4d",
                                    color: "white",
                                    border: "none",
                                    borderRadius: 5,
                                    padding: "5px 10px",
                                    cursor: "pointer",
                                }}
                            >
                                삭제
                            </button>
                        </div>
                    ))}
                </div>

                {selectedNote && (
                    <div style={{ marginTop: "20px" }}>
                        <h2>선택된 메모</h2>
                        <h3>{selectedNote.title}</h3>
                        <p>{selectedNote.content}</p>
                    </div>
                )}
            </div>

            {isModalOpen && (
                <div
                    style={{
                        position: "fixed",
                        width: "100%",
                        height: "100%",
                        backgroundColor: "rgba(0, 0, 0, 0.3)",
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                    }}
                >
                    <div style={{ backgroundColor: "white", width: "80%", height: "80%" }}>
                        <textarea
                            value={title}
                            onChange={(e) => setTitle(e.target.value)}
                            placeholder="제목"
                            style={{ width: "100%" }}
                        />
                        <textarea
                            value={content}
                            onChange={(e) => setContent(e.target.value)}
                            placeholder="내용"
                            style={{ width: "100%" }}
                        />
                        <button onClick={onAddOrUpdate}>저장</button>
                        <button onClick={closeModal}>취소</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default CombinedTodolist;
