import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
    const [formData, setFormData] = useState({ userId: '', password: '' });
    const [errors, setErrors] = useState({});
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const validationErrors = {};
        if (!formData.userId) validationErrors.userId = "아이디를 입력하세요.";
        if (!formData.password) validationErrors.password = "비밀번호를 입력하세요.";

        if (Object.keys(validationErrors).length === 0) {
            axios.get('http://localhost:8000/users')
                .then((result) => {
                    const user = result.data.find(user => user.userId === formData.userId);
                    if (user && user.password === formData.password) {
                        alert("로그인 되었습니다.");
                        navigate('/');
                    } else {
                        setErrors({
                            userId: user ? undefined : "아이디가 틀렸습니다.",
                            password: user ? "비밀번호가 틀렸습니다." : undefined
                        });
                    }
                })
                .catch(err => console.error(err));
        } else {
            setErrors(validationErrors);
        }
    };

    return (
        <div style={{ marginTop: '5rem', textAlign: 'center' }}>
            <h4>로그인</h4>
            <form onSubmit={handleSubmit} style={{ display: 'inline-block', textAlign: 'left' }}>
                <div>
                    <label htmlFor="userId">아이디:</label>
                    <input
                        type="text"
                        id="userId"
                        name="userId"
                        value={formData.userId}
                        onChange={handleChange}
                        style={{ display: 'block', marginBottom: '0.5rem' }}
                    />
                    {errors.userId && <span style={{ color: 'red' }}>{errors.userId}</span>}
                </div>

                <div>
                    <label htmlFor="password">비밀번호:</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        style={{ display: 'block', marginBottom: '0.5rem' }}
                    />
                    {errors.password && <span style={{ color: 'red' }}>{errors.password}</span>}
                </div>

                <button type="submit" style={{ marginTop: '1rem' }}>로그인</button>
                <button
                    type="button"
                    onClick={() => navigate('/join')}
                >
                    회원가입
                </button>
            </form>
        </div>
    );
}

export default Login;
