import React from 'react';

function Todo({ filteredTodos, openEditor, toggleFavorite, onDelete, restoreTodo, onRealDelete, favorites, selectedFolder, sortOrder, setSortOrder, query, setQuery, filterDate, setFilterDate, logInUser }) {
    return (
        <div style={{ flex: 1, padding: 20 }}>
            <h2>{logInUser && selectedFolder !== 1 ? selectedFolder.name : '폴더 선택'}</h2>
            <div>
                <input
                    type="text"
                    placeholder="검색 (제목/내용)"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    style={{
                        width: "30%",
                        padding: 10,
                        marginBottom: 20,
                        boxSizing: "border-box",
                    }} />
            </div>
            <div style={{ marginBottom: 15 }}>
                <input
                    type="date"
                    value={filterDate}
                    onChange={(e) => setFilterDate(e.target.value)}
                    style={{ marginRight: 10 }}
                />
                <button onClick={() => setSortOrder(sortOrder === "latest" ? "oldest" : "latest")}>
                    {sortOrder === "latest" ? "최신순" : "오래된 순"}
                </button>
            </div>
            <div
                style={{
                    display: "grid",
                    gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
                    gap: 20,
                }}>
                {logInUser ? (
                    filteredTodos.map((todo) => (
                        <div
                            key={todo.id}
                            style={{
                                border: "1px solid #ccc",
                                borderRadius: 10,
                                padding: 10,
                                backgroundColor: "white",
                            }}>
                            <h3
                                onClick={() => openEditor(todo)}>
                                {todo.title}
                            </h3>
                            <p style={{ fontSize: 14, color: "#555" }}>
                                {todo.content.length > 20 ? todo.content.slice(0, 20) + '...' : todo.content}
                            </p>
                            <p>{todo.date}</p>
                            <div style={{ display: "flex", justifyContent: "space-between", width: "100%" }}>
                                {selectedFolder !== 1 && (
                                    <button onClick={() => toggleFavorite(todo.id, todo.folderId)}>
                                        {favorites.some((item) => item.todoId === todo.id && item.folderId === todo.folderId) ? "★" : "☆"}
                                    </button>)}
                                {selectedFolder !== 1 && (
                                    <button onClick={() => onDelete(todo.id)}>
                                        삭제
                                    </button>
                                )}
                            </div>
                            <div style={{ display: "flex", justifyContent: "space-between", width: "100%" }}>
                                {selectedFolder === 1 && (
                                    <button
                                        onClick={() => restoreTodo(todo.id)}>
                                        복원
                                    </button>
                                )}
                                {selectedFolder === 1 && (
                                    <button onClick={() => onRealDelete(todo.id)}>
                                        완전 삭제
                                    </button>
                                )}
                            </div>
                        </div>
                    ))
                ) : (
                    <p>로그인 후 메모를 확인할 수 있습니다.</p>
                )}

            </div>
        </div>
    );
}

export default Todo;
