import React from 'react';

function FolderList({ folders, selectedFolder, setSelectedFolder, newFolderName, setNewFolderName, addFolder, onDeleteFolder, logInUser }) {
    return (
        <div style={{ width: "250px", borderRight: "2px solid #ccc", padding: 10, boxSizing: "border-box" }}>
            <h2>ToDoList</h2>
            {logInUser && (
                <p>안녕하세요, {logInUser.userId}님!</p>
            )}
            <ul style={{ listStyle: "none", padding: 0 }}>
                {logInUser ? (
                    folders.map((folder) => (
                        <li
                            key={folder.id}
                            onClick={() => setSelectedFolder(folder.id)}
                            style={{
                                display: "flex",
                                justifyContent: "space-between",
                                alignItems: "center",
                                fontWeight: selectedFolder === folder.id ? "bold" : "normal",
                                backgroundColor: selectedFolder === folder.id ? "#e0e0e0" : "transparent",
                                padding: "5px",
                                borderRadius: "4px",
                            }}>
                            <span>{folder.name}</span>
                            {folder.id !== 1 && (
                                <button
                                    onClick={(e) => {
                                        e.stopPropagation();
                                        onDeleteFolder(folder.id);
                                    }}>
                                    삭제
                                </button>
                            )}
                        </li>
                    ))
                ) : (
                    <p>로그인 후 폴더를 볼 수 있습니다.</p>
                )}
            </ul>

            <div>
                <input
                    type="text"
                    value={newFolderName}
                    onChange={(e) => setNewFolderName(e.target.value)}
                    placeholder="새 폴더 이름"
                    style={{ marginRight: 10 }} />
                <button onClick={addFolder}>폴더 추가</button>
            </div>
        </div>
    );
}

export default FolderList;
