import React, { useState, useEffect } from "react";
import axios from "axios";
import { chat } from './openai';
import { useNavigate } from 'react-router-dom';

function ToDoList() {
    const navigate = useNavigate();
    const [loggedInUser, setLoggedInUser] = useState(null);
    const [todos, setTodos] = useState({});
    const [folders, setFolders] = useState([
        { id: 1, name: "개인" },
        { id: 2, name: "업무" },
        { id: 3, name: "휴지통" },
    ]);
    const [favorites, setFavorites] = useState([]);
    const [selectedFolder, setSelectedFolder] = useState(1);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [editingTodo, setEditingTodo] = useState(null);
    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [newFolderName, setNewFolderName] = useState("");
    const [query, setQuery] = useState("");
    const [summary, setSummary] = useState("");
    const [sortOrder, setSortOrder] = useState("latest");
    const [filterDate, setFilterDate] = useState("");

    // 로그인 확인 및 사용자 정보 가져오기
    useEffect(() => {
        const userId = sessionStorage.getItem("loggedInUserId");
        if (!userId) {
            navigate("/login"); // 로그인 안 되어 있다면 리다이렉트
        } else {
            axios.get(`http://localhost:8000/users/${userId}`)
                .then((response) => {
                    setLoggedInUser(response.data);
                    setTodos(response.data.todos || {});
                })
                .catch((error) => {
                    console.error("사용자 정보를 가져오는 데 실패했습니다:", error);
                    navigate("/login"); // 오류 시 로그인 페이지로 이동
                });
        }
    }, [navigate]);

    const onLogout = () => {
        sessionStorage.removeItem("loggedInUserId"); // 로그아웃 시 sessionStorage 초기화
        navigate("/login");
    };

    const getCurrentDate = () => {
        const now = new Date();
        const year = now.getFullYear();
        const month = String(now.getMonth() + 1).padStart(2, "0");
        const date = String(now.getDate()).padStart(2, "0");
        const hours = String(now.getHours()).padStart(2, "0");
        const minutes = String(now.getMinutes()).padStart(2, "0");
        const seconds = String(now.getSeconds()).padStart(2, "0");

        return `${year}-${month}-${date} ${hours}:${minutes}:${seconds}`;
    };

    const onAddOrUpdate = () => {
        if (title.trim() === "" || content.trim() === "") {
            alert("제목과 내용을 입력하세요!");
            return;
        }
        const folderTodos = todos[selectedFolder] || [];
        if (editingTodo) {
            const updatedTodos = folderTodos.map((todo) =>
                todo.id === editingTodo.id ? { ...todo, title, content, filterDate } : todo
            );
            setTodos({ ...todos, [selectedFolder]: updatedTodos });
        } else {
            const id = folderTodos.length > 0 ? folderTodos[folderTodos.length - 1].id + 1 : 1;
            const updatedTodos = [...folderTodos, { id, title, content, date: getCurrentDate() }];
            setTodos({ ...todos, [selectedFolder]: updatedTodos });
        }
        closeModal();
    };

    const onDelete = (id) => {
        const folderTodos = todos[selectedFolder];
        const updatedTodos = folderTodos.filter((todo) => todo.id !== id);
        const todoToDelete = folderTodos.find((todo) => todo.id === id);
        setTodos({ ...todos, [selectedFolder]: updatedTodos });
        if (todoToDelete) {
            setTodos(prevState => ({
                ...prevState,
                3: [...prevState[3], todoToDelete],
            }));
        }
    };

    const onRealDelete = (id) => {
        const folderTodos = todos[selectedFolder];
        const updatedTodos = folderTodos.filter((todo) => todo.id !== id);
        setTodos({ ...todos, [selectedFolder]: updatedTodos });
    };

    const TRASH_FOLDER_ID = 3;

    const restoreTodo = (id) => {
        const trashTodos = todos[TRASH_FOLDER_ID];
        const todoToRestore = trashTodos.find((todo) => todo.id === id);

        if (todoToRestore) {
            const originalFolder = todoToRestore.previousFolder || 1;
            setTodos((prevState) => {
                const updatedTrashTodos = trashTodos.filter((todo) => todo.id !== id);
                const updatedOriginalFolderTodos = [
                    ...prevState[originalFolder],
                    { ...todoToRestore, date: filterDate },
                ];
                return {
                    ...prevState,
                    [TRASH_FOLDER_ID]: updatedTrashTodos,
                    [originalFolder]: updatedOriginalFolderTodos,
                };
            });
        }
    };

    const toggleFavorite = (todoId) => {
        setFavorites((prev) =>
            prev.includes(todoId)
                ? prev.filter((id) => id !== todoId)
                : [...prev, todoId]
        );
    };

    const openModal = (todo = null) => {
        setIsModalOpen(true);
        if (todo) {
            setEditingTodo(todo);
            setTitle(todo.title);
            setContent(todo.content);
            setFilterDate(todo.date);
        } else {
            setEditingTodo(null);
            setTitle("");
            setContent("");
            setFilterDate("");
        }
    };

    const closeModal = () => {
        setIsModalOpen(false);
        setEditingTodo(null);
        setTitle("");
        setContent("");
        setSummary("");
        setFilterDate("");
    };

    const sortTodos = (todos) => {
        return todos.sort((a, b) =>
            sortOrder === "latest"
                ? new Date(b.date) - new Date(a.date)
                : new Date(a.date) - new Date(b.date)
        );
    };

    const filteredTodos = sortTodos(
        (todos[selectedFolder] || []).filter(
            (todo) =>
                (!filterDate || todo.date === filterDate) &&
                (todo.title.toLowerCase().includes(query.toLowerCase()) ||
                    todo.content.toLowerCase().includes(query.toLowerCase()))
        )
    );

    const fetchSummary = () => {
        const prompt = `다음은 사용자가 작성한 TodoList입니다. 내용을 간결하고, 가독성 있게 정리해주세요.
        ${content}`;

        chat(prompt, (response) => setSummary(response));
    };

    const updateTodoContent = () => {
        setContent(summary);
    };

    return (
        <div style={{ display: "flex", height: "100vh" }}>
            {/* 폴더 리스트 */}
            <div
                style={{
                    width: "300px",
                    borderRight: "2px solid #ccc",
                    padding: 10,
                    boxSizing: "border-box",
                }}
            >
                <h2>ToDoList</h2>
                {loggedInUser && <p>안녕하세요, {loggedInUser.userId}님!</p>}
                <button onClick={onLogout}>로그아웃</button>
                <button
                    onClick={() => openModal()}
                >
                    메모 쓰기
                </button>

                <ul style={{ listStyle: "none", padding: 0 }}>
                    {folders.map((folder) => (
                        <li
                            key={folder.id}
                            onClick={() => setSelectedFolder(folder.id)}
                            style={{
                                cursor: "pointer",
                                fontWeight: selectedFolder === folder.id ? "bold" : "normal",
                                backgroundColor: selectedFolder === folder.id ? "#e0e0e0" : "transparent",
                                padding: "5px",
                                borderRadius: "4px"
                            }}
                        >
                            {folder.name}
                        </li>
                    ))}
                </ul>

                <div>
                    <input
                        type="text"
                        value={newFolderName}
                        onChange={(e) => setNewFolderName(e.target.value)}
                        placeholder="새 폴더 이름"
                    />
                    <button
                        onClick={() => {
                            if (newFolderName.trim()) {
                                const id = folders.length > 0 ? folders[folders.length - 1].id + 1 : 1;
                                setFolders([...folders, { id, name: newFolderName.trim() }]);
                                setNewFolderName("");
                            }
                        }}
                    >
                        폴더 추가
                    </button>
                </div>
                <h3>{folders.find((folder) => folder.id === selectedFolder)?.name} 폴더 즐겨찾기</h3>
                {favorites.map((id) => {
                    const note = todos[selectedFolder]?.find((todo) => todo.id === id);
                    return note ? (
                        <div key={id} onClick={() => openModal(note)}>
                            {note.title}
                        </div>
                    ) : null;
                })}
            </div>

            {/* 메모 영역 */}
            <div style={{ flex: 1, padding: 10 }}>
                <div style={{ display: "flex", marginBottom: 10 }}>
                    <input
                        type="text"
                        placeholder="검색..."
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        style={{ flex: 1, padding: 5, marginRight: 5 }}
                    />
                    <input
                        type="date"
                        value={filterDate}
                        onChange={(e) => setFilterDate(e.target.value)}
                        style={{ padding: 5 }}
                    />
                </div>
                <ul style={{ listStyle: "none", padding: 0 }}>
                    {filteredTodos.map((todo) => (
                        <li key={todo.id}>
                            <h4>{todo.title}</h4>
                            <p>{todo.content}</p>
                            <p>작성일: {todo.date}</p>
                            <button onClick={() => openModal(todo)}>편집</button>
                            <button onClick={() => onDelete(todo.id)}>삭제</button>
                            <button onClick={() => toggleFavorite(todo.id)}>즐겨찾기</button>
                        </li>
                    ))}
                </ul>
                <button onClick={fetchSummary}>요약</button>
                <button onClick={updateTodoContent}>요약된 내용으로 업데이트</button>
            </div>

            {/* 모달 */}
            {isModalOpen && (
                <div
                    style={{
                        position: "fixed",
                        top: "50%",
                        left: "50%",
                        transform: "translate(-50%, -50%)",
                        width: "400px",
                        padding: "20px",
                        backgroundColor: "white",
                        border: "1px solid #ccc",
                        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                    }}
                >
                    <h2>{editingTodo ? "메모 수정" : "새 메모 작성"}</h2>
                    <input
                        type="text"
                        placeholder="제목"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        style={{ width: "100%", padding: "5px", marginBottom: "10px" }}
                    />
                    <textarea
                        placeholder="내용"
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                        style={{ width: "100%", height: "100px", padding: "5px", marginBottom: "10px" }}
                    ></textarea>
                    <button onClick={onAddOrUpdate}>
                        {editingTodo ? "수정 저장" : "저장"}
                    </button>
                    <button onClick={closeModal} style={{ marginLeft: "10px" }}>
                        닫기
                    </button>
                </div>
            )}
        </div>
    );
}

export default ToDoList;
